import React, { useState } from 'react';
import { Row, Col, Card, Form, Button, Alert, ProgressBar } from 'react-bootstrap';

function TrustedContactPortal() {
  const [uploadStatus, setUploadStatus] = useState('idle'); // idle, uploading, processing, success, error
  const [verificationResult, setVerificationResult] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setUploadStatus('idle');
    setVerificationResult(null);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) return;

    setUploadStatus('uploading');
    
    // Simulate file upload and processing
    setTimeout(() => {
      setUploadStatus('processing');
      
      // Simulate Azure AI Vision processing
      setTimeout(() => {
        setUploadStatus('success');
        setVerificationResult({
          extractedName: 'John Doe',
          dateOfDeath: '2024-01-15',
          certificateId: 'DC-2024-001234',
          confidence: 0.95,
          status: 'verified'
        });
      }, 3000);
    }, 1000);
  };

  const getStatusMessage = () => {
    switch (uploadStatus) {
      case 'uploading':
        return 'Uploading death certificate...';
      case 'processing':
        return 'Azure AI Vision is processing the document...';
      case 'success':
        return 'Death certificate verified successfully!';
      case 'error':
        return 'Error processing death certificate. Please try again.';
      default:
        return '';
    }
  };

  return (
    <div>
      <h2 className="mb-4">Trusted Contact Portal</h2>
      <p className="text-muted mb-4">
        Upload a death certificate to verify a death event and initiate digital asset protection.
      </p>
      
      <Row>
        <Col md={8}>
          <Card>
            <Card.Header>
              <h5>Death Certificate Verification</h5>
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleUpload}>
                <Form.Group className="mb-3">
                  <Form.Label>Upload Death Certificate (PDF or Image)</Form.Label>
                  <Form.Control
                    type="file"
                    accept=".pdf,.jpg,.jpeg,.png"
                    onChange={handleFileSelect}
                    disabled={uploadStatus === 'uploading' || uploadStatus === 'processing'}
                  />
                  <Form.Text className="text-muted">
                    Supported formats: PDF, JPG, PNG. Maximum file size: 10MB.
                  </Form.Text>
                </Form.Group>
                
                {selectedFile && (
                  <Alert variant="info">
                    Selected file: {selectedFile.name} ({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)
                  </Alert>
                )}
                
                {uploadStatus !== 'idle' && (
                  <div className="mb-3">
                    <p>{getStatusMessage()}</p>
                    {(uploadStatus === 'uploading' || uploadStatus === 'processing') && (
                      <ProgressBar animated now={uploadStatus === 'uploading' ? 30 : 70} />
                    )}
                  </div>
                )}
                
                <Button 
                  type="submit" 
                  variant="primary"
                  disabled={!selectedFile || uploadStatus === 'uploading' || uploadStatus === 'processing'}
                >
                  {uploadStatus === 'uploading' || uploadStatus === 'processing' ? 'Processing...' : 'Verify Death Certificate'}
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={4}>
          {verificationResult && (
            <Card>
              <Card.Header>
                <h5>Verification Results</h5>
              </Card.Header>
              <Card.Body>
                <div className="mb-2">
                  <strong>Extracted Name:</strong><br />
                  {verificationResult.extractedName}
                </div>
                <div className="mb-2">
                  <strong>Date of Death:</strong><br />
                  {verificationResult.dateOfDeath}
                </div>
                <div className="mb-2">
                  <strong>Certificate ID:</strong><br />
                  {verificationResult.certificateId}
                </div>
                <div className="mb-2">
                  <strong>Confidence Score:</strong><br />
                  {(verificationResult.confidence * 100).toFixed(1)}%
                </div>
                <div className="mb-3">
                  <strong>Status:</strong><br />
                  <span className={`badge bg-${verificationResult.status === 'verified' ? 'success' : 'warning'}`}>
                    {verificationResult.status}
                  </span>
                </div>
                
                {verificationResult.status === 'verified' && (
                  <Alert variant="success">
                    <strong>Verification Complete!</strong><br />
                    Digital assets have been frozen and policies will be executed automatically.
                  </Alert>
                )}
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </div>
  );
}

export default TrustedContactPortal;
