import React, { useState } from 'react';
import { Row, Col, Card, Form, Button, Table, Badge, Tab, Tabs, Alert } from 'react-bootstrap';

function UserVault() {
  const [assets, setAssets] = useState([]);
  const [trustedContacts, setTrustedContacts] = useState([]);
  const [newAsset, setNewAsset] = useState({
    assetType: 'email',
    platformName: '',
    accountIdentifier: '',
    credentials: '',
    actionType: 'delete'
  });
  const [newContact, setNewContact] = useState({
    name: '',
    email: '',
    phone: '',
    relationship: ''
  });

  const handleAssetSubmit = (e) => {
    e.preventDefault();
    const asset = {
      ...newAsset,
      id: Date.now(),
      status: 'active',
      createdAt: new Date().toISOString()
    };
    setAssets([...assets, asset]);
    setNewAsset({
      assetType: 'email',
      platformName: '',
      accountIdentifier: '',
      credentials: '',
      actionType: 'delete'
    });
  };

  const handleContactSubmit = (e) => {
    e.preventDefault();
    const contact = {
      ...newContact,
      id: Date.now(),
      status: 'pending',
      createdAt: new Date().toISOString()
    };
    setTrustedContacts([...trustedContacts, contact]);
    setNewContact({
      name: '',
      email: '',
      phone: '',
      relationship: ''
    });
  };

  const removeAsset = (id) => {
    setAssets(assets.filter(asset => asset.id !== id));
  };

  const removeContact = (id) => {
    setTrustedContacts(trustedContacts.filter(contact => contact.id !== id));
  };

  return (
    <div>
      <h2 className="mb-4">Digital Asset Vault</h2>
      
      <Row>
        <Col md={6}>
          <Card>
            <Card.Header>
              <h5>Add New Digital Asset</h5>
            </Card.Header>
            <Card.Body>
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Asset Type</Form.Label>
                  <Form.Select 
                    value={newAsset.assetType}
                    onChange={(e) => setNewAsset({...newAsset, assetType: e.target.value})}
                  >
                    <option value="email">Email Account</option>
                    <option value="bank">Bank Account</option>
                    <option value="social_media">Social Media</option>
                    <option value="other">Other</option>
                  </Form.Select>
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Platform Name</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="e.g., Gmail, Chase Bank, Facebook"
                    value={newAsset.platformName}
                    onChange={(e) => setNewAsset({...newAsset, platformName: e.target.value})}
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Account Identifier</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="e.g., email address, account number"
                    value={newAsset.accountIdentifier}
                    onChange={(e) => setNewAsset({...newAsset, accountIdentifier: e.target.value})}
                    required
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Action Policy</Form.Label>
                  <Form.Select 
                    value={newAsset.actionType}
                    onChange={(e) => setNewAsset({...newAsset, actionType: e.target.value})}
                  >
                    <option value="delete">Delete Account</option>
                    <option value="memorialize">Memorialize Account</option>
                    <option value="transfer">Transfer to Contact</option>
                    <option value="lock">Lock Account</option>
                  </Form.Select>
                </Form.Group>
                
                <Button type="submit" variant="primary">
                  Add Asset
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
        
        <Col md={6}>
          <Card>
            <Card.Header>
              <h5>Registered Assets ({assets.length})</h5>
            </Card.Header>
            <Card.Body>
              {assets.length === 0 ? (
                <p className="text-muted">No assets registered yet.</p>
              ) : (
                <Table responsive>
                  <thead>
                    <tr>
                      <th>Platform</th>
                      <th>Type</th>
                      <th>Action</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {assets.map((asset) => (
                      <tr key={asset.id}>
                        <td>{asset.platformName}</td>
                        <td>
                          <Badge bg="secondary">
                            {asset.assetType.replace('_', ' ')}
                          </Badge>
                        </td>
                        <td>
                          <Badge bg="info">
                            {asset.actionType}
                          </Badge>
                        </td>
                        <td>
                          <Badge bg="success">
                            {asset.status}
                          </Badge>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default UserVault;
